          radial-gradient(50% 35% at 50% 30%, rgba(250, 210, 255, 0.05), transparent 70%);
        mix-blend-mode: screen;
        z-index: 1;
        animation: nebulaDrift 18s ease-in-out infinite alternate;
      }

      @keyframes nebulaDrift {
        0% {
          transform: translate3d(0, 0, 0) scale(1);
        }
        50% {
          transform: translate3d(-0.8%, 0.6%, 0) scale(1.01);
        }
        100% {
          transform: translate3d(0, 0, 0) scale(1);
        }
      }

      .star-canvas {
        position: absolute;
        inset: 0;
        width: 100%;
        height: 100%;
        border-radius: inherit;
        pointer-events: none;
        z-index: 0;
        will-change: transform, opacity; /* OPT: Êõ¥Âπ≥Êªë */
      }

      .aether-header {
        position: relative;
        padding: 14px 16px 14px 16px;
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: space-between;
        background: linear-gradient(135deg, rgba(28, 24, 44, 0.6), rgba(36, 24, 58, 0.55));
        border-bottom: 1px solid rgba(180, 160, 255, 0.16);
        transition: background 0.28s ease;
        outline: none;
        z-index: 2;
      }

      .aether-header:hover {
        background: linear-gradient(135deg, rgba(30, 26, 48, 0.75), rgba(40, 26, 62, 0.7));
      }

      .aether-title {
        display: flex;
        align-items: center;
        gap: 10px;
        font-size: 16px;
        font-weight: 700;
        color: #e9e3ff;
        letter-spacing: 0.3px;
        text-shadow: 0 1px 2px rgba(0, 0, 0, 0.35);
        position: relative;
      }

      .moon-wrap::after {
        content: '';
        position: absolute;
        width: 42px;
        height: 42px;
        border-radius: 50%;
        background: radial-gradient(
          circle,
          rgba(220, 208, 255, 0.35) 0%,
          rgba(198, 186, 255, 0.22) 40%,
          rgba(180, 160, 255, 0.12) 60%,
          transparent 75%
        );
        filter: blur(4px);
        opacity: 0.8;
        pointer-events: none;
        z-index: 0;
        animation: haloPulse 3.6s ease-in-out infinite alternate;
      }

      .moon-svg {
        width: 22px;
        height: 22px;
        display: block;
        position: relative;
        z-index: 2;
      }

      @keyframes haloPulse {
        from {
          transform: scale(0.95);
          opacity: 0.7;
        }
        to {
          transform: scale(1.05);
          opacity: 0.95;
        }
      }

      .particles {
        position: absolute;
        inset: -16px -16px -10px -10px;
        pointer-events: none;
        z-index: 1;
        contain: layout paint; /* OPT: ÈöîÁ¶ªÁ≤íÂ≠êÂÆπÂô®ÔºåÂáèÂ∞ëÈáçÊéíÂ∏¶Êù•ÁöÑÊäñÂä® */
      }

      .particles i {
        position: absolute;
        top: 50%;
        left: 50%;
        width: var(--size, 2px);
        height: var(--size, 2px);
        border-radius: 50%;
        background: radial-gradient(circle at 50% 50%, #fff 0%, #efe9ff 45%, rgba(255, 255, 255, 0) 70%);
        box-shadow: 0 0 6px rgba(190, 170, 255, 0.9), 0 0 12px rgba(160, 140, 240, 0.6);
        /* FIX: ‰ΩøÁî® translate3dÔºåÊèêÂçáÂä®ÁîªÁ®≥ÂÆöÊÄß‰∏éÊÄßËÉΩ */
        transform: translate3d(calc(-50% + var(--x, 0)), calc(-50% + var(--y, 0)), 0) scale(var(--s, 1));
        opacity: 0;
        animation: sparkle var(--twinkle, 3.2s) ease-in-out infinite, drift var(--drift, 7.5s) linear infinite;
        animation-delay: var(--delay, 0s), var(--delay, 0s);
        will-change: transform, opacity;
      }

      @keyframes sparkle {
        0%,
        90%,
        100% {
          opacity: 0;
        }
        10% {
          opacity: 1;
        }
        40% {
          opacity: 0.7;
        }
      }

      @keyframes drift {
        0% {
          transform: translate3d(calc(-50% + var(--x)), calc(-50% + var(--y)), 0) scale(calc(var(--s, 1) * 0.9));
        }
        50% {
          transform: translate3d(calc(-50% + var(--x) * 1.15), calc(-50% + var(--y) * 1.15), 0)
            scale(calc(var(--s, 1) * 1.1));
        }
        100% {
          transform: translate3d(calc(-50% + var(--x)), calc(-50% + var(--y)), 0) scale(calc(var(--s, 1) * 0.9));
        }
      }

      .toggle-icon {
        width: 28px;
        height: 28px;
        position: relative;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        transition: all 0.35s cubic-bezier(0.4, 0, 0.2, 1);
        flex-shrink: 0;
        cursor: pointer;
        /* ÂõõËßíÊòüÊòüÂΩ¢Áä∂ */
        clip-path: polygon(50% 0%, 61.8% 38.2%, 100% 50%, 61.8% 61.8%, 50% 100%, 38.2% 61.8%, 0% 50%, 38.2% 38.2%);
        background: radial-gradient(circle at center, rgba(255, 255, 255, 0.15) 0%, transparent 70%),
          linear-gradient(135deg, rgba(141, 121, 222, 0.6), rgba(109, 97, 184, 0.4));
        overflow: hidden;
      }

      /* ÊòüÊòüÂÜÖÈÉ®ÂÖâÊôï */
      .toggle-icon::after {
        content: '';
        position: absolute;
        inset: 2px;
        clip-path: inherit;
        background: radial-gradient(circle at center, rgba(255, 255, 255, 0.25) 0%, transparent 60%);
        opacity: 0.6;
        transition: opacity 0.3s ease;
      }


      /* ÊµÅÂÖâÊïàÊûú */
      .toggle-icon .shimmer {
        position: absolute;
        inset: 0;
        clip-path: inherit;
        background: linear-gradient(
          45deg,
          transparent 0%,
          transparent 40%,
          rgba(255, 255, 255, 0.6) 50%,
          rgba(255, 255, 255, 0.8) 52%,
          rgba(255, 255, 255, 0.6) 54%,
          transparent 60%,
          transparent 100%
        );
        transform: translateX(-100%);
        transition: transform 0.6s ease;
        pointer-events: none;
      }

      /* ÊÇ¨ÂÅúÊïàÊûú */
      .toggle-icon:hover {
        transform: scale(1.08);
      }

      .toggle-icon:hover::after {
        opacity: 0.9;
      }

      /* Â±ïÂºÄÁä∂ÊÄÅ */
      .aether-collapsible.expanded .toggle-icon {
        background: radial-gradient(circle at center, rgba(255, 255, 255, 0.2) 0%, transparent 70%),
          linear-gradient(135deg, rgba(185, 166, 255, 0.7), rgba(139, 121, 222, 0.5));
        transform: scale(1.1) rotate(180deg);
      }

      .aether-collapsible.expanded .toggle-icon::before {
        transform: rotate(180deg);
      }

      /* ÁÇπÂáªÊµÅÂÖâÂä®Áîª */
      .toggle-icon.clicked .shimmer {
        transform: translateX(100%);
      }

      /* ÊòüÊòüÈó™ÁÉÅÂä®Áîª */
      @keyframes starTwinkle {
        0%,
        100% {
          box-shadow: 0 0 12px rgba(120, 90, 190, 0.3), inset 0 1px 1px rgba(255, 255, 255, 0.2);
        }
        50% {
          box-shadow: 0 0 20px rgba(150, 120, 240, 0.5), 0 0 8px rgba(255, 255, 255, 0.3),
            inset 0 1px 2px rgba(255, 255, 255, 0.25);
        }
      }

      /* ‰∏∫ÊòüÊòüÊ∑ªÂä†ÂæÆÂ¶ôÁöÑÈó™ÁÉÅÊïàÊûú */
      .toggle-icon {
        animation: starTwinkle 4s ease-in-out infinite;
      }

      /* Â±ïÂºÄÁä∂ÊÄÅ‰∏ãÁöÑËÑâÂÜ≤Âä®Áîª */
      @keyframes expandedPulse {
        0%,
        100% {
          box-shadow: 0 0 16px rgba(150, 120, 240, 0.6), 0 0 8px rgba(255, 255, 255, 0.3),
            inset 0 1px 2px rgba(255, 255, 255, 0.25);
        }
        50% {
          box-shadow: 0 0 24px rgba(180, 150, 255, 0.8), 0 0 12px rgba(255, 255, 255, 0.4),
            inset 0 1px 3px rgba(255, 255, 255, 0.3);
        }
      }

      .aether-collapsible.expanded .toggle-icon {
        animation: expandedPulse 3s ease-in-out infinite;
      }

      .aether-content {
        max-height: 0;
        overflow: hidden;
        transition: max-height 0.38s cubic-bezier(0.4, 0, 0.2, 1);
        /* OPT: Èôç‰Ωé‰∏çÈÄèÊòéÂ∫¶ÔºåËÆ©‰∏ãÊñπÊòüÁ©∫Ëã•ÈöêËã•Áé∞ */
        background: linear-gradient(180deg, rgba(18, 16, 28, 0.45), rgba(18, 16, 28, 0.6));
        position: relative;
        z-index: 2;
      }

      /* OPT: ÊûÅÂÖâ/Êòü‰∫ëË¶ÜÂ±ÇÔºåÂ±ïÂºÄÊó∂Ê∑°ÂÖ• */
      .aether-content::before {
        content: '';
        position: absolute;
        inset: 0;
        pointer-events: none;
        background: radial-gradient(80% 50% at 95% -10%, rgba(150, 110, 255, 0.18), transparent 65%),
          radial-gradient(70% 45% at 10% 0%, rgba(90, 130, 255, 0.16), transparent 65%),
          linear-gradient(180deg, rgba(255, 255, 255, 0.04), rgba(255, 255, 255, 0));
        mix-blend-mode: screen;
        opacity: 0;
        transition: opacity 0.35s ease;
      }

      .aether-collapsible.expanded .aether-content::before {
        opacity: 1;
      }

      .aether-content-inner {
        padding: 18px;
        color: #d8d3f6;
        line-height: 1.65;
        font-size: 14px;
        border-top: 1px solid rgba(180, 160, 255, 0.12);
        position: relative;
        white-space: pre-wrap;
        word-wrap: break-word;
        word-break: break-word;
        overflow-wrap: break-word;
        text-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);
        max-height: 300px;
        overflow-y: auto;
      }

      /* Ëá™ÂÆö‰πâÊªöÂä®Êù°Ê†∑Âºè */
      .aether-content-inner::-webkit-scrollbar {
        width: 6px;
      }

      .aether-content-inner::-webkit-scrollbar-track {
        background: rgba(30, 26, 48, 0.4);
        border-radius: 3px;
      }

      .aether-content-inner::-webkit-scrollbar-thumb {
        background: linear-gradient(180deg, #8b79de, #6d61b8);
        border-radius: 3px;
        box-shadow: 0 0 4px rgba(120, 90, 190, 0.3);
      }

      .aether-content-inner::-webkit-scrollbar-thumb:hover {
        background: linear-gradient(180deg, #9d88e5, #8b79de);
      }

      /* Êî∂Ëµ∑ÊåâÈíÆÊ†∑Âºè */
      .aether-collapse {
        padding: 12px 18px;
        text-align: center;
        color: #b8a5ff;
        font-size: 13px;
        cursor: pointer;
        transition: all 0.25s ease;
        border-top: 1px solid rgba(180, 160, 255, 0.08);
        position: relative;
        user-select: none;
        background: linear-gradient(180deg, rgba(18, 16, 28, 0.3), rgba(18, 16, 28, 0.6));
      }

      .aether-collapse:hover {
        color: #d8d3f6;
        background: linear-gradient(180deg, rgba(22, 18, 32, 0.5), rgba(22, 18, 32, 0.8));
      }

      .aether-collapse::before {
        content: '‚úß';
        margin-right: 6px;
        font-size: 11px;
        opacity: 0.6;
        filter: drop-shadow(0 0 3px rgba(150, 120, 240, 0.4));
      }

      .aether-collapse::after {
        content: '‚úß';
        margin-left: 6px;
        font-size: 11px;
        opacity: 0.6;
        filter: drop-shadow(0 0 3px rgba(150, 120, 240, 0.4));
      }

      .aether-content-inner::before {
        content: '‚úß';
        position: absolute;
        top: 10px;
        right: 14px;
        font-size: 12px;
        color: #cbb8ff;
        opacity: 0.7;
        filter: drop-shadow(0 0 4px rgba(150, 120, 240, 0.6));
      }

      @media (max-width: 480px) {
        .aether-collapsible {
          max-width: 100%;
          margin: 0 5px;
          border-radius: 16px;
        }
        .aether-header {
          padding: 12px 14px;
        }
        .aether-title {
          font-size: 15px;
          gap: 8px;
        }
        .moon-wrap {
          width: 24px;
          height: 24px;
        }
        .moon-svg {
          width: 20px;
          height: 20px;
        }
        .aether-content-inner {
          padding: 14px;
          font-size: 13px;
        }
      }

      @media (prefers-reduced-motion: reduce) {
        .aether-collapsible,
        .toggle-icon,
        .toggle-icon::before,
        .aether-content,
        .moon-wrap::after,
        .particles i,
        .aether-collapsible::before,
        .toggle-icon .shimmer {
          animation: none !important;
          transition: none !important;
        }
        .aether-content::before {
          opacity: 0.8;
        } /* ‰øùÊåÅË£ÖÈ•∞Â±ÇÔºåÈÅøÂÖçÁ™ÅÂÖÄ */
      }
    </style>
  </head>
  <body>
    <div class="aether-collapsible" id="aetherCollapsible">
      <canvas class="star-canvas" id="starCanvas" aria-hidden="true"></canvas>
      <div
        class="aether-header"
        onclick="toggleCollapsible()"
        role="button"
        aria-expanded="false"
        aria-controls="aetherContent"
        tabindex="0"
      >
        <div class="aether-title">
          <span class="moon-wrap" aria-hidden="true">
            <svg class="moon-svg" viewBox="0 0 64 64" aria-hidden="true">
              <defs>
                <radialGradient id="crescentGrad" cx="32%" cy="34%" r="70%">
                  <stop offset="0%" stop-color="#ffffff" />
                  <stop offset="42%" stop-color="#f6f2ff" />
                  <stop offset="78%" stop-color="#cec1ff" />
                  <stop offset="100%" stop-color="#b59fff" />
                </radialGradient>
                <filter id="moonGlow" x="-80%" y="-80%" width="260%" height="260%">
                  <feGaussianBlur stdDeviation="2.8" result="blur" />
                  <feMerge>
                    <feMergeNode in="blur" />
                    <feMergeNode in="SourceGraphic" />
                  </feMerge>
                </filter>
                <mask id="crescentMask">
                  <rect x="0" y="0" width="64" height="64" fill="black" />
                  <circle cx="28" cy="32" r="22" fill="white" />
                  <circle cx="39" cy="28" r="22" fill="black" />
                </mask>
              </defs>
              <g filter="url(#moonGlow)">
                <circle cx="28" cy="32" r="22" fill="url(#crescentGrad)" mask="url(#crescentMask)" />
                <circle
                  cx="28"
                  cy="32"
                  r="22"
                  fill="none"
                  stroke="rgba(255,255,255,0.35)"
                  stroke-width="0.7"
                  mask="url(#crescentMask)"
                />
                <g opacity="0.25" mask="url(#crescentMask)">
                  <circle cx="22" cy="26" r="1.2" fill="#fff" />
                  <circle cx="24.5" cy="34" r="0.9" fill="#efe9ff" />
                  <circle cx="30" cy="29" r="0.7" fill="#fff" />
                </g>
              </g>
            </svg>
            <span class="particles" aria-hidden="true"></span>
          </span>
          Aether
        </div>
        <div class="toggle-icon" aria-hidden="true">
          <div class="shimmer"></div>
        </div>
      </div>

      <div class="aether-content" id="aetherContent">
        <div class="aether-content-inner">$1</div>
        <div class="aether-collapse" onclick="toggleCollapsible()">Êî∂Ëµ∑</div>
      </div>
    </div>

    <script>
      let starApi;

      function toggleCollapsible() {
        const collapsible = document.getElementById('aetherCollapsible');
        const header = collapsible.querySelector('.aether-header');
        const content = document.getElementById('aetherContent');
        const contentInner = content.querySelector('.aether-content-inner');
        const collapseButton = content.querySelector('.aether-collapse');
        const toggleIcon = header.querySelector('.toggle-icon');
        const isExpanded = collapsible.classList.contains('expanded');

        // Ëß¶ÂèëÊµÅÂÖâÊïàÊûú
        toggleIcon.classList.add('clicked');
        setTimeout(() => {
          toggleIcon.classList.remove('clicked');
        }, 600);

        if (isExpanded) {
          collapsible.classList.remove('expanded');
          header.setAttribute('aria-expanded', 'false');
          content.style.maxHeight = '0px';
        } else {
          collapsible.classList.add('expanded');
          header.setAttribute('aria-expanded', 'true');
          // ËÆ°ÁÆóÂÜÖÂÆπÂå∫ÂüüÈ´òÂ∫¶ÔºöÂÜÖÂÆπÂå∫Âüü(ÊúÄÂ§ß300px) + Êî∂Ëµ∑ÊåâÈíÆÈ´òÂ∫¶ + ËæπÊ°ÜÂíåÂÜÖËæπË∑ù
          const innerHeight = Math.min(contentInner.scrollHeight, 300);
          const buttonHeight = collapseButton.offsetHeight;
          const totalHeight = innerHeight + buttonHeight + 2; // +2 ‰∏∫border
          content.style.maxHeight = totalHeight + 'px';
        }
        // Á®çÂÄôÂÜçË∞ÉÊï¥ iframe ÂíåÊòüÁ©∫ÔºåÂáèÂ∞ëÊäñÂä®
        setTimeout(updateIframeHeight, 420);
      }

      function updateIframeHeight() {
        if (starApi && starApi.resize) starApi.resize(); // FIX: Âè™Âú®Â∞∫ÂØ∏ÂèòÊõ¥Êó∂ÂÜÖÈÉ®ÁúüÊ≠£ÈáçÊéí
        const bodyHeight = document.body.scrollHeight;
        if (window.parent && window.parent !== window) {
          window.parent.postMessage({ type: 'resizeIframe', height: bodyHeight }, '*');
        }
      }

      function initParticles() {
        const container = document.querySelector('.particles');
        if (!container) return;
        const count = 16;
        for (let i = 0; i < count; i++) {
          const p = document.createElement('i');
          const angle = Math.random() * Math.PI * 2;
          const radius = 10 + Math.random() * 16;
          const x = Math.cos(angle) * radius;
          const y = Math.sin(angle) * radius;
          const size = 1 + Math.random() * 2.6;
          const delay = (Math.random() * 2.5).toFixed(2) + 's';
          const twinkle = (2.4 + Math.random() * 2.4).toFixed(2) + 's';
          const drift = (6 + Math.random() * 4).toFixed(2) + 's';
          const scale = (0.8 + Math.random() * 0.6).toFixed(2);
          p.style.setProperty('--x', x.toFixed(2) + 'px');
          p.style.setProperty('--y', y.toFixed(2) + 'px');
          p.style.setProperty('--size', size.toFixed(2) + 'px');
          p.style.setProperty('--delay', delay);
          p.style.setProperty('--twinkle', twinkle);
          p.style.setProperty('--drift', drift);
          p.style.setProperty('--s', scale);
          container.appendChild(p);
        }
      }

      function initStarCanvas() {
        const canvas = document.getElementById('starCanvas');
        if (!canvas) return {};
        const ctx = canvas.getContext('2d');
        let w = 0,
          h = 0,
          dpr = 1,
          stars = [],
          rafId = 0,
          animate = true;
        let cssW = 0,
          cssH = 0; // FIX: ËÆ∞ÂΩï‰∏äÊ¨° CSS Â∞∫ÂØ∏ÔºåÈÅøÂÖçÊó†ÂèòÂåñÊó∂ÈáçÊéí

        function createStar() {
          const x = Math.random() * w;
          const y = Math.random() * h;
          const r = (Math.random() * 1.1 + 0.3) * dpr;
          const depth = Math.random();
          const baseAlpha = 0.45 + Math.random() * 0.45;
          const amp = 0.25 + Math.random() * 0.35;
          const tw = 0.0015 + Math.random() * 0.003;
          const driftX = (0.02 + depth * 0.1) * dpr;
          const driftY = (0.005 + depth * 0.03) * dpr;
          const phase = Math.random() * Math.PI * 2;
          return { x, y, r: r * (0.8 + depth * 0.6), baseAlpha, amp, tw, driftX, driftY, phase };
        }

        function targetCountFor(area) {
          return Math.min(220, Math.round(area / 2200));
        }

        function resize() {
          const rect = canvas.getBoundingClientRect();
          const nextDpr = Math.min(window.devicePixelRatio || 1, 2);
          const nextCssW = rect.width;
          const nextCssH = rect.height;

          // Ê≤°ÊúâÂÆûÈôÖ CSS Â∞∫ÂØ∏ÂèòÂåñÂàô‰∏çÈáçÊéíÔºàÂ±ïÂºÄÊó∂ÈÅøÂÖçÈó™ÁÉÅÔºâ
          if (Math.round(nextCssW) === Math.round(cssW) && Math.round(nextCssH) === Math.round(cssH)) {
            return;
          }

          const prevW = w;
          const prevH = h;
          const prevDpr = dpr;

          dpr = nextDpr;

          canvas.width = Math.max(1, Math.floor(nextCssW * dpr));
          canvas.height = Math.max(1, Math.floor(nextCssH * dpr));
          canvas.style.width = nextCssW + 'px';
          canvas.style.height = nextCssH + 'px';

          w = canvas.width;
          h = canvas.height;

          const area = Math.max(1, nextCssW * nextCssH);
          const target = targetCountFor(area);

          // FIX: ‰øùÁïôÂπ∂ÊåâÊØî‰æãÁº©ÊîæÁé∞ÊúâÊòüÊòüÔºåÈÅøÂÖç‚Äú‰π±Âä®‚Äù
          if (prevW > 0 && prevH > 0) {
            const scaleX = w / prevW;
            const scaleY = h / prevH;
            const dprRatio = dpr / (prevDpr || dpr);
            for (const s of stars) {
              s.x *= scaleX;
              s.y *= scaleY;
              s.r *= dprRatio;
              s.driftX *= dprRatio;
              s.driftY *= dprRatio;
            }
          }

          // Âπ≥ÊªëÂ¢ûÂáèÊï∞ÈáèÔºàÂè™Ë°•Â∑ÆÊàñË£ÅÂâ™Ôºå‰∏çÈáçÂª∫ÊâÄÊúâÔºâ
          if (stars.length < target) {
            const toAdd = target - stars.length;
            for (let i = 0; i < toAdd; i++) stars.push(createStar());
          } else if (stars.length > target) {
            stars.length = target;
          }

          cssW = nextCssW;
          cssH = nextCssH;
        }

        function step() {
          if (!animate) return;
          ctx.clearRect(0, 0, w, h);
          for (const s of stars) {
            s.phase += s.tw;
            s.x += s.driftX;
            s.y -= s.driftY * 0.2;
            if (s.x > w + 2) s.x -= w + 4;
            if (s.y < -2) s.y += h + 4;
