      function toggleCollapsible() {
        const collapsible = document.getElementById('aetherCollapsible');
        const header = collapsible.querySelector('.aether-header');
        const isExpanded = collapsible.classList.toggle('expanded');
        header.setAttribute('aria-expanded', isExpanded ? 'true' : 'false');
        updateIframeHeight();
      }

      function updateIframeHeight() {
        const bodyHeight = document.body.scrollHeight;
        if (window.parent && window.parent !== window) {
          window.parent.postMessage({ type: 'resizeIframe', height: bodyHeight }, '*');
        }
      }

      document.addEventListener('keydown', function (event) {
        if (event.key === 'Enter' || event.key === ' ') {
          const activeElement = document.activeElement;
          if (activeElement && activeElement.classList.contains('aether-header')) {
            event.preventDefault();
            toggleCollapsible();
          }
        }
      });
      document.querySelector('.aether-header').setAttribute('tabindex', '0');

      window.addEventListener('load', updateIframeHeight);
    </script>
