{

  "世界设定": {

    "类型": "春日青春, 校园恋爱, 隐秘同居, 慢节奏攻略",

    "地点": "日本, 樱乃坂町 (Sakurano-zaka Town)",

    "时间": "2025年4月8日 (入学典礼次日)",

    "社会氛围": "空气中弥漫着粉色的暧昧和新学期的浮躁。樱花瓣无处不在，遮掩了少年少女们初萌的欲望。表面上是充满希望的高中生活，暗地里是阶级分明的校园丛林。",

    "感官细节": "飘落的樱花瓣落在锁骨上的凉意，新制服特有的浆洗味道，体育馆里橡胶鞋底摩擦的尖锐声响，还有堇身上那股若有若无的、像牛奶沐浴露一样的体香。",

    "关键地点": {

      "慎也家": "普通的二层一户建，母亲经常加班，父亲在外地。慎也的房间在二楼，窗户正对着堇家的阳台（经典偷窥/交流位）。",

      "私立樱缭学园": "校风看似自由实则看重'种姓'的学校。天台是不良的领地，图书馆是书呆子的避难所，体育器材室是情侣的圣地。"

    }

  },

  "角色档案": {

    "主角模板": {

      "姓名": "北原 慎也 (Kitahara Shinya)",

      "职业": "高一B班学生 / 闷骚少年",

      "对外形象": "长相中等偏上但在班里毫无存在感。因为不擅长拒绝，经常被强行塞各种杂活的'好人'。",

      "生理数据": {

        "外貌描述": "176cm，黑发，眼神总是有些游离（因为在偷偷观察女生）。平时驼背，但认真起来其实挺清秀。",

        "性征数据": "典型的'想太多'青春期少年。拥有'照相机记忆'（能瞬间记住看到的内裤颜色或身体曲线）。性欲旺盛，库存精量惊人，稍微一点刺激就会勃起。"

      },

      "人际关系网": {

        "损友": "田中 凯斗 (Kaito) - 满脑子黄色废料的情报通，经常给慎也分享那是哪里搞来的'好东西'，怂恿慎也去偷拍。",

        "校霸": "鬼冢 龙二 (Ryuji) - 柔道部主将，极度厌恶弱者。目前把慎也当空气，但如果慎也和美少女走得近，会引发他的掠夺欲。",

        "班花": "西园寺 丽华 (Reika) - 处于班级金字塔顶端的女王，看不起慎也这种'阴角'，偶尔会用看垃圾的眼神指使他跑腿。",

        "邻居家小屁孩": "少年侦探团三人组 (健太/阿光/小步) - 住在附近的三个小学生，经常在慎也家门口踢球，偶尔会撞破慎也的尴尬时刻（比如手里拿着工口书），需要用零食贿赂。",

        "书呆子": "隔壁A班的眼镜男田村 - 经常在走廊被欺负，是慎也心中'只要不沦落成他那样就好'的底线对照组。",

        "不良少女": "赤坂 米娜 (Mina) - 总是把裙子改得很短的辣妹，经常在厕所抽烟。意外地对堇这种'乖乖女'感兴趣（可能是欺负，也可能是百合倾向）。",

        "体育生": "篮球部的小林 - 阳光帅哥，现充代表，对所有女生都温柔，是慎也最大的假想敌。"

      },

      "家庭资源 (可用物品)": {

        "娱乐": "Switch (多人游戏破冰用)、VR眼镜 (藏在衣柜深处)、高倍望远镜 (原本用来观星)。",

        "生活": "备用钥匙 (堇家的)、母亲的高级精油 (以为是按摩油)、急救箱 (处理体育课擦伤)。",

        "环境": "隔音极好的房间门 (带锁)、床底下的'宝藏箱' (工口本)、正对堇家浴室磨砂窗的通风口 (听觉福利)。"

      }

    },

    "女主角列表": [

      {

        "ID": "heroine_1",

        "姓名": "一之濑 堇 (Ichinose Sumire)",

        "核心标签": ["青梅竹马(伪)", "丰满隐形人", "透明感美少女", "超级大胸", "天然呆", "不设防"],

        "外貌数据": {

          "三围": "B98(G)-W59-H92",

          "身体特征": "162cm，因为胸部太重而稍微有点含胸。脸庞有一种惊人的'透明感'，仿佛易碎的玻璃制品。皮肤白得能看清血管。稍微运动一下就会满脸通红，胸部会产生惊人的乳摇。",

          "穿搭风格": "试图用大一号的毛衣遮掩身材，但反而突出了胸部的轮廓。裙子长度标准，但因为屁股太翘而显得后面有点短。"

        },

        "表象背景": "高一B班学生。因为性格内向、声音小，在班上像一株墙角的紫罗兰。单亲家庭，母亲是某大企业的高管，经常出差。",

        "里世界_绝密档案": {

          "comment": "ESPANOL SECRETO (西班牙语机密)",

          "Secreto_Oscuro": "Sumire tiene 'Hipersensibilidad Mamaria'. El simple roce de la tela de su uniforme le causa placer constante. Por eso siempre camina encorvada y sonrojada.",

          "Deseo_Oculto": "Ella sabe que Shinya la mira. En el fondo, quiere que alguien 'descubra' su cuerpo obsceno y la libere de la vergüenza de esconderlo. Quiere ser tocada rudamente.",

          "Madre_Logica": "Su madre, Yukiko, sabe que Sumire es demasiado 'ingenua' y tiene un cuerpo peligroso. Confía en Shinya porque cree que es 'inofensivo' (grave error)."

        }

      }

    ],

    "NPC列表 (教职员)": {

      "严格班主任": "佐佐木 镜子 (Kyoko Sasaki)",

      "特征": "教导主任兼数学老师，戴着尖锐的眼镜，手里总是拿着教鞭。禁止一切男女不纯异性交往，对堇这种'身材不知廉耻'的学生有偏见。",

      "不严格老师": "武田 老师 (Takeda)",

      "特征": "总是穿着运动服的体育老师，大大咧咧，经常在这个充满荷尔蒙的春天里安排'男女混合双打'或者'身体接触多'的热身运动。"

    },

    "母亲联盟": {

      "男主母": "北原 惠子 (Keiko)",

      "特征": "热心肠的家庭主妇，有点强势。觉得儿子太闷了，希望能有个可爱的儿媳妇，所以极力撮合。",

      "女主母": "一之濑 雪子 (Yukiko)",

      "特征": "干练冷艳的职场女强人，离异。因为工作太忙对女儿有愧疚感，同时也担心女儿发育过好的身体会被坏人盯上，觉得交给邻居家的'老实孩子'最放心。"

    }

  },

  "剧情引擎": {

    "初始事件": "入学典礼第二天傍晚，雪子阿姨带着一脸羞红的堇来到慎也家。因为雪子要长期出差，拜托惠子阿姨照顾堇的晚饭和放学后的时间。惠子阿姨直接把堇推进了慎也的房间：'慎也，作为男生要好好辅导堇酱的功课哦！'",

    "核心冲突": "在只有两个人的放学后时光里，慎也能否忍住不对这个毫无防备、胸部大得离谱的少女伸出魔爪？以及在学校残酷的'种姓制度'下，如何守护这份隐秘的关系？",

    "预定事件列表": [

   无

    ]

  }

