    <script>
        // åŠ¨æ€è®¡ç®—é«˜åº¦
        function updateHeight() {
            // ç»™ä¸€ç‚¹ç¼“å†²ç©ºé—´ï¼Œé˜²æ­¢æ»šåŠ¨æ¡é—ªçƒ
            const h = document.body.scrollHeight + 10;
            // å°è¯•é€šçŸ¥çˆ¶çº§è°ƒæ•´ï¼ˆè™½ç„¶CSS min-heighté€šå¸¸èµ·ä¸»è¦ä½œç”¨ï¼‰
            if(window.parent) {
                // è¿™æ˜¯ä¸€ä¸ªé€šç”¨çš„iframeé€šä¿¡å°è¯•ï¼Œå…·ä½“å–å†³äºæ’ä»¶å®ç°ï¼Œä½†ä¸ä¼šæŠ¥é”™
                window.parent.postMessage({ type: 'resize', height: h }, '*');
            }
        }

        // åˆ‡æ¢Tab
        function switchTab(id, btn) {
            // ç§»é™¤æ¿€æ´»çŠ¶æ€
            document.querySelectorAll('.tab-pane').forEach(el => el.classList.remove('active'));
            document.querySelectorAll('.nav-item').forEach(el => el.classList.remove('active'));

            // æ¿€æ´»æ–°çŠ¶æ€
            document.getElementById(id).classList.add('active');
            btn.classList.add('active');

            // ç«‹å³è§¦å‘é«˜åº¦é‡ç®—
            setTimeout(updateHeight, 50);
        }

        // æ¸²æŸ“ç‰©å“èƒ¶å›Š
        function renderInventory() {
            const raw = document.getElementById('raw-inv').innerText;
            const container = document.getElementById('render-inv');
            // æ¸…æ´—ï¼šå»é™¤ "ğŸ“¦ å…³é”®ç‰©å“ï¼š" è¿™ç±»å‰ç¼€ï¼Œæ›¿æ¢ä¸­æ–‡å†’å·
            let text = raw.replace(/ğŸ“¦.*?[:ï¼š]/g, '').replace(/ğŸ“±.*?[:ï¼š]/g, '|');

            // åˆ†å‰²å¹¶è¿‡æ»¤ç©ºé¡¹
            const items = text.split(/\||\\n/).filter(t => t.trim().length > 1);

            if(items.length === 0) {
                container.innerHTML = '<div style="color:#999; font-style:italic; text-align:center; padding:20px;">æš‚æ— èµ„äº§æ•°æ®</div>';
                return;
            }

            let html = '';
            items.forEach(item => {
                html += `<div class="item-capsule">${item.trim()}</div>`;
            });
            container.innerHTML = html;
        }

        // æ¸²æŸ“æ¬²æœ›åˆ—è¡¨
        function renderDesire() {
            const raw = document.getElementById('raw-desire').innerText;
            const lines = raw.split('\\n').filter(l => l.trim().length > 0);
            let html = '';
            lines.forEach(l => {
                html += `<div class="desire-row"><span class="desire-icon">â¤</span><span>${l.trim()}</span></div>`;
            });
            document.getElementById('render-desire').innerHTML = html;
        }

        // åˆå§‹åŒ–
        window.addEventListener('DOMContentLoaded', () => {
            renderInventory();
            renderDesire();
            updateHeight();
            // ç›‘å¬å›¾ç‰‡åŠ è½½å®Œæˆï¼Œé˜²æ­¢é«˜åº¦å¡Œé™·
            document.querySelector('img').onload = updateHeight;
        });

        // ç›‘å¬å¤§å°å˜åŒ–ï¼ˆå…¼å®¹Tabåˆ‡æ¢åŠ¨ç”»å¯¼è‡´çš„å»¶è¿Ÿï¼‰
        new ResizeObserver(updateHeight).observe(document.body);
    </script>
</body>
