    <script>
        (function() {
            // --- 1. åå­—ä¸å¤´åƒå¤„ç† ---
            const nameEl = document.getElementById('char-name');
            let rawName = nameEl.getAttribute('data-name') || "";

            // æ¸…æ´—åå­—: å»é™¤ [ ] { } ä»¥åŠé¦–å°¾ç©ºæ ¼
            // ä¾‹å¦‚: Name: [ä¸€ä¹‹æ¿‘å ‡] -> ä¸€ä¹‹æ¿‘å ‡
            let cleanName = rawName.replace(/[\[\]\{\}ã€ã€‘]/g, '').trim();

            // æ˜¾ç¤ºåå­—
            nameEl.innerText = cleanName || "Unknown";

            // åŠ è½½å¤´åƒ
            try {
                const map = window.top.ST_IMAGES || {};
                // å°è¯•åŒ¹é…: æ¸…æ´—åçš„åå­—, æˆ–å»é™¤æ‰€æœ‰ç©ºæ ¼çš„åå­—
                let url = map[cleanName] || map[cleanName.replace(/\s+/g, '')] || map['default'];
                if(url) document.getElementById('avatar').src = url;
            } catch(e){}

            // --- 2. é€šç”¨åˆ—è¡¨è§£æ (Stats, Visuals, Obs) ---
            function parseUniList(rawId, targetId) {
                const rawEl = document.getElementById(rawId);
                if (!rawEl) return;
                const text = rawEl.innerText;

                let html = '<div class="uni-list">';
                text.split('\n').forEach(line => {
                    line = line.trim();
                    if(!line) return;

                    let icon = '';
                    let rest = line;

                    // ç»ˆæ Emoji è¯†åˆ«é€»è¾‘
                    const emojiRegex = /^(\p{Extended_Pictographic}|\p{Emoji_Presentation}|[\u200d\ufe0f])+/u;
                    const match = line.match(emojiRegex);

                    if (match) {
                        icon = match[0]; 
                        rest = line.substring(icon.length).trim();
                    } else {
                        icon = 'â€¢'; 
                    }

                    const parts = rest.split(/[:ï¼š]/);
                    if (parts.length > 1) {
                        const label = parts[0].trim();
                        const val = parts.slice(1).join(':').trim();
                        html += `
                            <div class="uni-item">
                                <div class="uni-icon">${icon}</div>
                                <div class="uni-content">
                                    <span class="uni-label">${label}</span>
                                    <div class="uni-text">${val}</div>
                                </div>
                            </div>`;
                    } else {
                        html += `
                            <div class="uni-item">
                                <div class="uni-icon">${icon}</div>
                                <div class="uni-content">
                                    <div class="uni-text">${rest}</div>
                                </div>
                            </div>`;
                    }
                });
                html += '</div>';
                document.getElementById(targetId).innerHTML = html;
            }

            // --- 3. æ¨æ¼”è§£æ (Simulations) ---
            function parseAction() {
                const rawEl = document.getElementById('raw-4');
                if (!rawEl) return;
                const text = rawEl.innerText;

                let html = '<div class="act-list">';
                text.split('\n').forEach(line => {
                    // å»æ‰åºå· (1. 2. ç­‰)
                    let clean = line.replace(/^\d+[\.ï¼]\s*/, '').trim();
                    if(!clean) return;

                    // æ ¼å¼é€šå¸¸æ˜¯: è‹¥æˆ‘ [Action]: [Reaction] -> [Result]
                    // æˆ–è€…ç®€å•çš„: Trigger: React

                    let parts = clean.split(/[:ï¼š]/);
                    let trigger = parts[0].trim();
                    let react = parts.length > 1 ? parts.slice(1).join(':').trim() : clean;

                    // å¦‚æœæ²¡æœ‰å†’å·ï¼Œå°è¯•æ™ºèƒ½è¯†åˆ« "è‹¥æˆ‘"
                    if (parts.length <= 1) {
                         const actMatch = clean.match(/(?:è‹¥æˆ‘|If I)\s*(?:\[|ã€)(.*?)(?:\]|ã€‘)/i);
                         if (actMatch) {
                             trigger = "è‹¥æˆ‘ " + actMatch[1];
                             react = clean.replace(actMatch[0], '').trim();
                         } else {
                             trigger = "æ¨æ¼”";
                         }
                    }

                    html += `<div class="act-item"><div class="act-trigger">${trigger}</div><div class="act-react">${react}</div></div>`;
                });
                html += '</div>';
                document.getElementById('p4').innerHTML = html;
            }

            // --- æ‰§è¡Œæ¸²æŸ“ ---
            // p1: Stats (raw-1)
            // p2: Visuals (raw-2)
            // p3: Obs (raw-3)
            // p4: Sims (raw-4)
            // æ³¨æ„ï¼šæ ¹æ®ç”¨æˆ·ä¹ æƒ¯ï¼ŒVisuals å¯èƒ½æ›´é€‚åˆæ”¾åœ¨ p2ï¼ŒObs æ”¾åœ¨ p3
            parseUniList('raw-1', 'p1');
            parseUniList('raw-2', 'p2');
            parseUniList('raw-3', 'p3');
            parseAction();

        })();

        // --- äº¤äº’é€»è¾‘ ---
        window.switchTab = function(tabId, navEl) {
            document.querySelectorAll('.tab-pane').forEach(el => el.classList.remove('active'));
            document.getElementById(tabId).classList.add('active');

            document.querySelectorAll('.nav-item').forEach(el => el.classList.remove('active'));
            navEl.classList.add('active');

            updateHeight();
        };

        function updateHeight() {
            if(window.parent) {
                const h = document.body.scrollHeight;
                window.parent.postMessage({type: 'resize', height: h + 20}, '*');
            }
        }

        new ResizeObserver(updateHeight).observe(document.body);
        setTimeout(updateHeight, 100);
    </script>
</body>
</html>
