    <script>
        (function() {
            const raw = document.getElementById('raw-content').innerText;

            // --- 1. æå–åå­— (Name) ---
            // æ”¯æŒ Name: xxx æˆ– Name: [xxx] æˆ– Name: {xxx}
            const nameMatch = raw.match(/Name\s*[:ï¼š]\s*(?:\[|\{)?(.*?)(?:\]|\})?\s*(?:\n|$)/i);
            const name = nameMatch ? nameMatch[1].trim() : "Unknown";
            document.getElementById('char-name').innerText = name;

            // å¤´åƒé€»è¾‘
            try {
                const map = window.top.ST_IMAGES || {};
                // å°è¯•åŒ¹é…: åå­—æœ¬èº«, æˆ–å»é™¤ç©ºæ ¼çš„åå­—
                let url = map[name] || map[name.replace(/\s+/g, '')] || map['default'];
                if(url) document.getElementById('avatar').src = url;
            } catch(e){}

            // --- 2. æå–å„ä¸ªæ¿å— (Sections) ---
            // è¾…åŠ©å‡½æ•°ï¼šæå–ä¸¤ä¸ªæ ‡è®°ä¹‹é—´çš„å†…å®¹
            function extractSection(startMarkers, endMarkers) {
                // æ„å»ºå¼€å§‹æ ‡è®°æ­£åˆ™: (?:\[Stats\]|\[Visuals\])
                const startRegex = new RegExp(`(?:\\[|\\*\\*|\\ã€)\\s*(?:${startMarkers.join('|')})\\s*(?:\\]|\\*\\*|\\ã€‘)`, 'i');
                const match = raw.match(startRegex);
                if (!match) return "";

                const startIndex = match.index + match[0].length;
                const rest = raw.substring(startIndex);

                // å¯»æ‰¾æœ€è¿‘çš„ç»“æŸæ ‡è®° (ä¸‹ä¸€ä¸ª Section çš„å¼€å§‹)
                // å¸¸è§çš„ Section Headers
                const allHeaders = ['Stats', 'Visuals', 'Observations', 'Deductions', 'Simulations', 'Name'];
                // è¿‡æ»¤æ‰å½“å‰çš„ headers
                const otherHeaders = allHeaders.filter(h => !startMarkers.some(sm => sm.includes(h)));

                const endRegex = new RegExp(`(?:\\[|\\*\\*|\\ã€)\\s*(?:${otherHeaders.join('|')}).*?(?:\\]|\\*\\*|\\ã€‘)`, 'i');
                const endMatch = rest.match(endRegex);

                if (endMatch) {
                    return rest.substring(0, endMatch.index).trim();
                } else {
                    return rest.trim(); // ç›´åˆ°æœ«å°¾
