                    // b. å¯¹ç¬¬ä¸€éƒ¨åˆ†(Time)è¿›è¡Œæ¸…æ´—: å»æ‰ "å±¥å†_x:", å»æ‰ Emoji

                    let parts = line.split(/[|ï½œ]/).map(p => {
                        return p.trim()
                            .replace(/^[\[ã€\(]+/, '') // å»æ‰å¼€å¤´çš„æ‹¬å·
                            .replace(/[\]ã€‘\)]+[ã€‚ï¼Œ\.,]?$/, ''); // å»æ‰ç»“å°¾çš„æ‹¬å·
                    }).filter(p => p);

                    if (parts.length > 0) {
                        // æ¸…æ´—ç¬¬ä¸€éƒ¨åˆ† (Time)
                        // å»æ‰ "å±¥å†_1:", "1.", "ğŸ“…" ç­‰
                        parts[0] = parts[0].replace(/^.*?[:ï¼š]\s*/, '') // å»æ‰å†’å·å‰ç¼€
                                           .replace(/^[\d\.\s]+/, '')   // å»æ‰æ•°å­—åºå·
                                           .replace(/^(\p{Extended_Pictographic}|\p{Emoji_Presentation})\s*/u, '') // å»æ‰è‡ªå¸¦ Emoji
                                           .trim();
