                        } else if (parts.length >= 5) {
                            // Time | Target | Location | Action | Result
                            // è¿™ç§æƒ…å†µä¸‹ï¼ŒæŠŠ Location å½“ä½œ Action çš„ä¸€éƒ¨åˆ†ï¼Œæˆ–è€…åˆå¹¶æ˜¾ç¤º
                            // è®©æˆ‘ä»¬æŠŠç¬¬3ä¸ª(Location)ä½œä¸º Action Tagï¼Œç¬¬4ã€5ä¸ªåˆå¹¶ä¸ºæè¿°
                            action = parts[2]; // Location
                            desc = parts.slice(3).join('ï¼Œ'); // Action + Result
                        } else {
                            // Fallback
                            desc = parts.slice(2).join(' ');
                        }

                        let tags = '';
                        if (time) tags += `<span class="meta-tag mt-time">ğŸ“… ${time}</span>`;
                        if (target) tags += `<span class="meta-tag mt-target">ğŸ‘¤ ${target}</span>`;
                        // å¦‚æœ Action çœ‹èµ·æ¥åƒåœ°ç‚¹ (å§å®¤, å®¢å…)ï¼Œæ¢ä¸ªå›¾æ ‡ï¼Ÿæˆ–è€…ç»Ÿä¸€ç”¨ ğŸ“/ğŸ®
                        // ç®€å•èµ·è§ï¼Œç»Ÿä¸€ç”¨ ğŸ®ï¼Œæˆ–è€…æ ¹æ®å†…å®¹åˆ¤æ–­
                        if (action) {
                            let actIcon = 'ğŸ®';
                            if (['å§å®¤', 'å®¢å…', 'æ•™å®¤', 'ç¥ç¤¾', 'å…¬å›­'].some(loc => action.includes(loc))) actIcon = 'ğŸ“';
                            tags += `<span class="meta-tag mt-act">${actIcon} ${action}</span>`;
                        }

                        html += `
                            <div class="hist-entry">
                                <div class="hist-meta">${tags}</div>
                                <div class="hist-text">${desc || action || target}</div>
                            </div>`;
                    } else {
                        // åªæœ‰ä¸€è¡Œæ–‡æœ¬çš„æƒ…å†µ
                        html += `<div class="hist-entry"><div class="hist-text">${line}</div></div>`;
                    }
                });

                container.innerHTML = html;
            }

            // --- æ‰§è¡Œæå–ä¸æ¸²æŸ“ ---
            const rawStatus = extractSection(['Status', 'çŠ¶æ€']);
            const rawAssets = extractSection(['Assets', 'Territory', 'Resources', 'èµ„äº§', 'é¢†åœ°', 'èµ„æº']);
            const rawInventory = extractSection(['Inventory', 'ç‰©å“']);
            const rawDesires = extractSection(['Desires', 'æ¬²æœ›']);
            const rawHistory = extractSection(['History', 'å±¥å†']);

            parseList(rawStatus, 'tab-status');
            parseList(rawAssets, 'tab-assets');
            parseInventory(rawInventory, 'tab-inventory');
            parseList(rawDesires, 'tab-desires');
            parseHistory(rawHistory, 'tab-history');

        })();

        // --- äº¤äº’é€»è¾‘ ---
        window.switchTab = function(tabId, navEl) {
            // 1. åˆ‡æ¢ Tab å†…å®¹
            document.querySelectorAll('.tab-pane').forEach(el => el.classList.remove('active'));
            document.getElementById(tabId).classList.add('active');

            // 2. åˆ‡æ¢å¯¼èˆªé«˜äº®
            document.querySelectorAll('.nav-item').forEach(el => el.classList.remove('active'));
            navEl.classList.add('active');

            // 3. è§¦å‘é«˜åº¦æ›´æ–°
            updateHeight();
        };

        function updateHeight() {
            if(window.parent) {
                const h = document.body.scrollHeight;
                window.parent.postMessage({type: 'resize', height: h + 20}, '*');
            }
        }

        // åˆå§‹åŒ–é«˜åº¦ç›‘å¬
